import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
class Maze extends React.Component{
  constructor(props){
    super(props);
    this.state = {width: 0,height: 0};
    this.rows = this.rows.bind(this);
  }

  componentDidMount(){
    var width = prompt('Please enter width.');
    var height = prompt('Please enter height');
    this.setState({width: width,height: height});
  }
 rows(){
    var width = Number(this.state.width);
    var height = Number(this.state.height);
    let table = [];
    let random_places = [];
    for(let k = 0;k < width;k++){ random_places.push(Math.floor(Math.random() * (width * height) ));}
    for (let i = 0; i < width; i++) {
      let children = []
      for (let j = 0; j < height; j++) {
        if(random_places.includes(i*j)){
          children.push(<td >{0}</td>);
         }else{
          children.push(<td ></td>);
         }

      }
      table.push(<tr>{children}</tr>)
    }
    return table;
  }
  render(){

  return(<div> 
              <table border='1'>
                 <tbody>
                   {this.rows()}
                 </tbody>
               </table>
        </div>);
  }
}
ReactDOM.render(
  <React.StrictMode>
    <Maze />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
