import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
class Maze extends React.Component{
  constructor(props){
    super(props);
    this.state = {width: 0,height: 0,currid: 0};
    this.rows = this.rows.bind(this);
    this.onKeyDown = this.onKeyDown.bind(this);
  }

  componentDidMount(){
    var width = prompt('Please enter width.');
    var height = prompt('Please enter height');
    this.setState({width: width,height: height}); 
    document.addEventListener("keydown", this.onKeyDown, false);
  }
  componentWillUnmount(){
    document.removeEventListener("keydown", this.onKeyDown, false);
  }
 rows(){
    var width = Number(this.state.width);
    var height = Number(this.state.height);
    let table = [];
    let random_places = [];
    var cw = Math.ceil(width/2);
    var ch = Math.ceil(height/2);
    var c = 1;

    for(let k = 1;k < width;k++){ random_places.push(Math.floor(Math.random() * (width * height) ));}
    for (let i = 1; i <= width; i++) {
      let children = []
      for (let j = 1; j <= height; j++) {
        if(i == cw && j== ch){
          children.push(<td id={c} className="active">
                            <div onKeyPress={this.handleKeyPress}>1</div>
                        </td>);            
        }else if(random_places.includes(i*j)){
          children.push(<td id={c}>{0}</td>);
         }else{
          children.push(<td id={c}></td>);
         }
        c++;
      }
      table.push(<tr>{children}</tr>)
    }
    return table;
  }
  onKeyDown(e) {
    var noc = document.getElementById('myTable').getElementsByTagName("td").length;
    var ele =document.querySelector('.active').id
    var rowSelected = table.getElementsByTagName('tr')[ele];
            rowSelected.style.backgroundColor = "yellow";
            rowSelected.className += " selected";
    switch (e.which) {

        case 37: var s= document.getElementById(ele);
                 s.value = '2';
          break;

        case 38: alert("up");// Up
            break;

        case 39: alert("right");// Right
            break;

        case 40: alert("down");// Down
            break;

        default:
            break;

    }
  }   
  render(){

  return(<div > 
              <table border='1' id="myTable">
                 <tbody>
                   {this.rows()}
                 </tbody>
               </table>
        </div>);
  }
}
ReactDOM.render(
  <React.StrictMode>
    <Maze />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
